mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY FINDDOG_NO DESC) AS RNUM, A.* FROM ( SELECT * FROM TB_FINDDOG
AND TITLE LIKE CONCAT('%', #{searchMap.title}, '%')
AND CATEGORY LIKE CONCAT('%', #{searchMap.category}, '%')
AND STATE LIKE CONCAT('%', #{searchMap.state}, '%')
AND AREA LIKE CONCAT('%', #{searchMap.area}, '%')
) AS A ) AS SubQuery WHERE RNUM BETWEEN #{pageDto.start} AND #{pageDto.end}
