mapper.select:
SELECT * FROM tb_question
AND HELP_CATEGORY_CODE = #{categoryCode}
AND HELP_CATEGORY_CODE = 'HEL-001'

SELECT COUNT(*) AS CNT FROM TB_INQUIRY
AND TITLE LIKE CONCAT('%', #{pageDto.keyword}, '%')
AND HELP_CATEGORY_CODE = #{categoryCode}

SELECT * FROM (
SELECT
ROW_NUMBER() OVER (ORDER BY INQUIRY_NO DESC) AS RNUM,
A.*
FROM (
SELECT *
FROM TB_INQUIRY


        AND TITLE LIKE CONCAT('%', #{pageDto.keyword}, '%')


        AND HELP_CATEGORY_CODE = #{categoryCode}


) AS A
LEFT JOIN TB_HELP_CATEGORY AS HC ON A.HELP_CATEGORY_CODE = HC.HELP_CATEGORY_CODE
) AS SubQuery
WHERE RNUM BETWEEN  #{pageDto.start} AND #{pageDto.end}
