mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY INQUIRY_NO DESC) AS RNUM, A.* FROM ( SELECT * FROM TB_INQUIRY
AND TITLE LIKE CONCAT('%', #{searchMap.title}, '%')
AND HELP_CATEGORY_CODE LIKE CONCAT('%', #{searchMap.category}, '%')
AND RESULT LIKE CONCAT('%', #{searchMap.result}, '%')
AND WRITER_ID LIKE CONCAT('%', #{searchMap.writer}, '%')
) AS A LEFT JOIN TB_HELP_CATEGORY AS HC ON A.HELP_CATEGORY_CODE = HC.HELP_CATEGORY_CODE ) AS SubQuery WHERE RNUM BETWEEN #{pageDto.start} AND #{pageDto.end}

mapper.update:
UPDATE TB_INQUIRY SET
    RESULT = #{result}
WHERE INQUIRY_NO = #{inquiryNo}

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY QUESTION_NO DESC) AS RNUM, A.* FROM ( SELECT * FROM TB_QUESTION
AND TITLE LIKE CONCAT('%', #{searchMap.title}, '%')
AND HELP_CATEGORY_CODE LIKE CONCAT('%', #{searchMap.category}, '%')
) AS A LEFT JOIN TB_HELP_CATEGORY AS HC ON A.HELP_CATEGORY_CODE = HC.HELP_CATEGORY_CODE ) AS SubQuery WHERE RNUM BETWEEN #{pageDto.start} AND #{pageDto.end}

mapper.update:
UPDATE TB_INQUIRY SET
  TITLE = #{title},
  HELP_CATEGORY_CODE = #{helpCategoryCode},
  CONTENT = #{content}
WHERE INQUIRY_NO = #{inquiryNo}

UPDATE TB_QUESTION SET
    HELP_CATEGORY_CODE = #{helpCategoryCode},
    QUESTION_CONTENT = #{questionContent},
    ANSWER_CONTENT = #{answerContent}
WHERE QUESTION_NO = #{questionNo}
