mapper.select:
SELECT precord_no AS precordNo, user_no AS userNo, title, content, viewcount
FROM tb_precord
WHERE precord_no = #{precordNo}

mapper.insert:
INSERT INTO tb_precord
( user_no , title, content, viewcount)
VALUES(#{userNo}, #{title}, #{content}, #{viewcount});

mapper.update:
UPDATE tb_precord
SET user_no= #{userNo}, title= #{title}, content= #{content}
WHERE precord_no= #{precordNo};

mapper.select:
SELECT patrol_no AS patrolNo, user_no AS userNo, name, age, breed, location
FROM tb_patrol
WHERE patrol_no = #{patrolNo};

mapper.update:
UPDATE tb_patrol
SET  name=#{name}, age=#{age}, breed=#{breed}, location=#{location}
WHERE patrol_no=#{patrolNo};
